mapper.select:
select 

from score
where stu_id = #{stuId,jdbcType=INTEGER}
  and exp_id = #{expId,jdbcType=INTEGER}

mapper.delete:
delete from score
where stu_id = #{stuId,jdbcType=INTEGER}
  and exp_id = #{expId,jdbcType=INTEGER}

mapper.insert:
insert into score (stu_id, exp_id, score
  )
values (#{stuId,jdbcType=INTEGER}, #{expId,jdbcType=INTEGER}, #{score,jdbcType=SMALLINT}
  )

mapper.update:
update score

  
    score = #{score,jdbcType=SMALLINT},
  

where stu_id = #{stuId,jdbcType=INTEGER}
  and exp_id = #{expId,jdbcType=INTEGER}

update score
set score = #{score,jdbcType=SMALLINT}
where stu_id = #{stuId,jdbcType=INTEGER}
  and exp_id = #{expId,jdbcType=INTEGER}

mapper.select:
SELECT u.id as stuId, stu_num, stu_name, e.name as expName, score, stu_class as stuClass, m.name as majorName, e.id as expId

  into outfile #{path}

FROM user u, experiment e, score s, major m
WHERE u.id = s.stu_id 
AND s.exp_id = e.id
AND u.stu_num = #{userId,jdbcType=INTEGER}
AND u.major_id=m.id

  AND e.id = #{expId,jdbcType=INTEGER}

SELECT u.id as stuId, stu_num, stu_name, major.name as majorName, stu_class, exp.name as expName, score, exp.id as expId

  into outfile #{path}

FROM user u, major, experiment exp, score s
WHERE u.major_id = major.id
AND s.stu_id = u.id
AND exp.id = s.exp_id
AND u.major_id = #{majorId,jdbcType=INTEGER}

  AND exp.id = #{expId,jdbcType=INTEGER}


  AND stu_class = #{stuClass, jdbcType=INTEGER}

ORDER BY #{orderBy}
